import React from "react";
import WeatherIcon from "./WeatherIcon";
import "./Weather.css";
import axios from "axios";

export default function WeatherForecast(props) {
  function getForecast(response) {
    return console.log(response.data);
  }

  let apiKeyForecast = `c95d60a1e3adbeb286133f1ebebc2579`;
  let lat = props.coordinates.lat;
  let lon = props.coordinates.long;
  let apiUrlForecast = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${apiKeyForecast}&units=metric`;
  axios.get(apiUrlForecast).then(getForecast);

  return (
    <div className="WeatherForecast">
      <div className="row pt-5">
        <div className="col-md-2 text-center">
          <div className="ForecastDayName">Monday</div>
          <div className="py-3">
            <WeatherIcon code={"11d"} size={70} />
          </div>
          <div className="ForecastDateTemperature">
            <span className="ForecastDateTemperature-min">14</span> °{" "}
            <span className="ForecastDateTemperature-max">20</span> °
          </div>
        </div>
      </div>
    </div>
  );
}
