import React from "react";
import WeatherIcon from "./WeatherIcon";

export default function WeatherForecastDay(props) {
  function maxTemp() {
    let temp = Math.round(props.data.temp.max);
    return `${temp}`;
  }
  function minTemp() {
    let temp = Math.round(props.data.temp.min);
    return `${temp}`;
  }

  return (
    <div className="WeatherForecastDay">
      <div className="ForecastDayName">{props.data.dt}</div>
      <div className="py-3">
        <WeatherIcon code={props.data.weather[0].icon} size={70} />
      </div>
      <div className="ForecastDateTemperature">
        <span className="ForecastDateTemperature-min">{minTemp()}</span> °{" "}
        <span className="ForecastDateTemperature-max">{maxTemp()}</span> °
      </div>
    </div>
  );
}
