import React, { useState } from "react";
import "./Weather.css";
import axios from "axios";
import WeatherInfo from "./WeatherInfo";
import WeatherForecast from "./WeatherForecast";

export default function Weather(props) {
  let [weatherData, setweatherData] = useState({ ready: false });
  const [city, setCity] = useState(props.defaultCity);

  function handleResponse(response) {
    setweatherData({
      ready: true,
      coordinates: response.data.coord,
      temperature: response.data.main.temp,
      wind: response.data.wind.speed,
      city: response.data.name,
      humidity: response.data.main.humidity,
      feelslike: response.data.main.feels_like,
      description: response.data.weather[0].description,
      icon: response.data.weather[0].icon,
      date: new Date(response.data.dt * 1000),
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    search();
  }

  function handleCityChance(event) {
    setCity(event.target.value);
  }

  function search() {
    const apiKey = "ba322d86c6e375290a924f7f5aba942e";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
  }

  if (weatherData.ready) {
    return (
      <div className="Weather">
        <div className="conteiner">
          <form id="search-form" onSubmit={handleSubmit}>
            <div class="input-group mb-3">
              <input
                type="text"
                class="form-control bg-transparent text-white border-light border-opacity-25 shadow-none"
                placeholder="City name"
                aria-label="City name"
                aria-describedby="button-addon2"
                onChange={handleCityChance}
              />
              <div class="input-group-append">
                <button
                  class="btn btn-success border-light border-opacity-25 ml-2"
                  type="submit"
                  id="button-addon2"
                >
                  Search
                </button>
              </div>
            </div>
          </form>
          <WeatherInfo info={weatherData} />
          <WeatherForecast coordinates={weatherData.coordinates} />
        </div>
      </div>
    );
  } else {
    search();
    return "Loading...";
  }
}
