import React from "react";
import WeatherIcon from "./WeatherIcon";

export default function WeatherForecastDay(props) {
  function maxTemp() {
    let temp = Math.round(props.data.temp.max);
    return `${temp}`;
  }

  function minTemp() {
    let temp = Math.round(props.data.temp.min);
    return `${temp}`;
  }

  function day() {
    let date = new Date(props.data.dt * 1000);
    let day = date.getDay();
    let days = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    return days[day];
  }

  function load() {
    return (
      <div className="WeatherForecastDay">
        <div className="ForecastDayName">{day()}</div>
        <div className="py-3 dayForecast">
          <WeatherIcon code={props.data.weather[0].icon} size={50} />
        </div>
        <div className="ForecastDateTemperature">
          <span className="ForecastDateTemperature-min">{minTemp()}</span> °{" "}
          <span className="ForecastDateTemperature-max">{maxTemp()}</span> °
        </div>
      </div>
    );
  }

  return load();
}
